	/**
	 * Method perform an INSERT on the database, given a FsStacks or Criteria object.
	 *
	 * @param      mixed $values Criteria or FsStacks object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseFsStacksPeer:doInsert:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseFsStacksPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(FsStacksPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from FsStacks object
		}

		if ($criteria->containsKey(FsStacksPeer::STACK_ID) && $criteria->keyContainsValue(FsStacksPeer::STACK_ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.FsStacksPeer::STACK_ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		
    foreach (sfMixer::getCallables('BaseFsStacksPeer:doInsert:post') as $callable)
    {
      call_user_func($callable, 'BaseFsStacksPeer', $values, $con, $pk);
    }

    return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a FsStacks or Criteria object.
	 *
	 * @param      mixed $values Criteria or FsStacks object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseFsStacksPeer:doUpdate:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseFsStacksPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(FsStacksPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(FsStacksPeer::STACK_ID);
			$selectCriteria->add(FsStacksPeer::STACK_ID, $criteria->remove(FsStacksPeer::STACK_ID), $comparison);

		} else { // $values is FsStacks object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);
	

    foreach (sfMixer::getCallables('BaseFsStacksPeer:doUpdate:post') as $callable)
    {
      call_user_func($callable, 'BaseFsStacksPeer', $values, $con, $ret);
    }

    return $ret;
  }

